import React from 'react'
import { useRouteError } from 'react-router-dom';
import classes from './ErrorPage.module.css';

function ErrorPage() {

  const errorDetails = useRouteError();
  let errorData = JSON.parse(errorDetails.data);
  let errorMessage = errorData.message || 'Something went wrong!';
  let errorStatusText = errorDetails.statusText || 'Something went wrong!'; 
  let errorStatus = errorDetails.status || '500'; 


  console.log(useRouteError());
  return (
    <div  className={`absolutePositioned ${classes.errorPage} ${
      errorStatus === '404'
        ? classes.notFound
        : errorStatus === '500'
        ? classes.serverError
        : classes.genericError
    }`}>
     <h1>{errorStatus} - {errorStatusText}</h1>
    <p>{errorMessage}</p>
    <p>An error occurred. Please try again later.</p>
    </div>
  )
}

export default ErrorPage
